package com.yq.consumer;import lombok.extern.slf4j.Slf4j;import org.apache.kafka.clients.consumer.ConsumerRecord;import org.springframework.kafka.annotation.KafkaListener;import org.springframework.stereotype.Component;import java.util.Optional;/** * Created by yqbjtu on 2018/4/22. */@Component@Slf4jpublic class CmdReceiverV2 {    @KafkaListener(topicPattern = "bbb.*")    public void listen(ConsumerRecord<?, ?> record) {        Optional<?> kafkaMessage = Optional.ofNullable(record.value());        if (kafkaMessage.isPresent()) {            Object message = kafkaMessage.get();            log.info("----------------- recordDev =" + record);            log.info("------------------ messageDev =" + message);        }    }}